import { useState } from "react";
import styles from "./styles.module.css";

const Main = () => {
	const [inputText, setInputText] = useState("");
	const [outputText, setOutputText] = useState("");
	const [isLoading, setIsLoading] = useState(false);

	const handleLogout = () => {
		localStorage.removeItem("token");
		window.location.reload();
	};

	const handleSubmit = async () => {
		if (!inputText.trim()) return;
		setIsLoading(true);

		try {
			// Define the prompt to send with the user input
			const prompt = `Simplify the following text for better readability while preserving meaning:\n\n"${inputText}"`;

			const response = await fetch("http://localhost:5001/api/gpt4", {
				method: "POST",
				headers: { "Content-Type": "application/json" },
				body: JSON.stringify({ prompt }),
			});

			const data = await response.json();
			setOutputText(data?.response?.replace(/^"|"$/g, '') || "No response received.");
		} catch (error) {
			console.error("Error fetching GPT-4 response:", error);
			setOutputText("An error occurred while fetching the response.");
		}

		setIsLoading(false);
	};

	return (
		<>
			<nav className={styles.navbar}>
				<h1>Text Simplification Study</h1>
				<button className={styles.white_btn} onClick={handleLogout}>
					Logout
				</button>
			</nav>
			<div className={styles.main_container}>
				<div className={styles.form_container}>
					<textarea
						className={styles.textarea}
						placeholder="Original Text"
						value={inputText}
						onChange={(e) => setInputText(e.target.value)}
					></textarea>
					<button
						className={styles.submit_btn}
						onClick={handleSubmit}
						disabled={isLoading}
					>
						{isLoading ? "Processing..." : "Submit"}
					</button>
					{/* <p className={styles.or_text}>OR</p>
					<button className={styles.upload_btn}>Upload Document</button> */}

					{outputText && (
						<textarea
							className={styles.output_box}
							readOnly
							value={outputText}
						></textarea>
					)}
				</div>
			</div>
		</>
	);


};

export default Main;
