import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import styles from "./styles.module.css";

const handleLogout = () => {
  localStorage.removeItem("token");
  window.location.reload();
};

const SurveyPage = () => {
  const { state } = useLocation();
  const { email, inputText } = state || {};
  const [data, setData] = useState(null);

  useEffect(() => {
    const fetchSimplification = async () => {
      try {
        const response = await fetch("http://localhost:8080/api/simplifications/fetch", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ email, inputText }),
        });

        console.log("response", response);

        const result = await response.json();
        setData(result.data);
      } catch (error) {
        console.error("Error fetching simplification data:", error);
      }
    };

    if (email && inputText) {
      fetchSimplification();
    }
  }, [email, inputText]);

  if (!data) return <p>Loading...</p>;

  const { inputText: input, outputText, editedText } = data;

  return (
    <>
		{/* Navbar */}
		<nav className={styles.navbar}>
		  <h1>Text Simplification Study</h1>
		  <button className={styles.white_btn} onClick={handleLogout}>
			Logout
		  </button>
		</nav>

    <div className={styles.survey_container}>
      <textarea value={input} readOnly placeholder="Input Text" />
      <textarea value={outputText} readOnly placeholder="Initial Generated Text" />
      {outputText !== editedText && (
        <textarea value={editedText} readOnly placeholder="Final Edited Text" />
      )}
      <div>
        <h3>Survey</h3>
        <form>
          {/* Survey questions*/}
        </form>
      </div>
    </div>
    </>
  );
};

export default SurveyPage;
