// src/components/Survey/index.jsx
import React, { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-routerâ€‘dom";
import DiffMatchPatch                   from "diff-match-patch";
import { saveAs }                       from "file-saver";
import styles                           from "./styles.module.css";

const dmp = new DiffMatchPatch();

const wordCount = txt => (txt ? txt.trim().split(/\s+/).filter(Boolean).length : 0);

export default function SurveyPage() {
  /* ------------------------------------------------------------------ */
  /* 1.  Data received from Review.jsx                                  */
  /* ------------------------------------------------------------------ */
  const {
    email,
    inputText          = "",      // original user input
    generatedText      = "",      // AIâ€‘generated text (v1)
    finalText          = "",      // text that was on screen when user hit â€œSaveâ€
    saveHistory        = [],      // optional array of previous Save clicks
  } = useLocation().state || {};

  /* ------------------------------------------------------------------ */
  /* 2.  Local state â€“ sideâ€‘bar, diff toggles, survey answers, etc.     */
  /* ------------------------------------------------------------------ */
  const [latestFinal, setLatestFinal] = useState(finalText);
  const [selectedVersion, setSelectedVersion] = useState(saveHistory.length); // 1â€‘based
  const [showDiffIn,  setShowDiffIn]  = useState(false); // input â†” AI
  const [showDiffOut, setShowDiffOut] = useState(false); // AI â†” final
  const [diffHtmlIn,  setDiffHtmlIn]  = useState("");
  const [diffHtmlOut, setDiffHtmlOut] = useState("");
  const [sidebar,     setSidebar]     = useState(false);
  const [submitted,   setSubmitted]   = useState(false);

  const navigate = useNavigate();

  /* ------------------------------------------------------------------ */
  /* 3.  Derived values (word counts, diffs)                            */
  /* ------------------------------------------------------------------ */
  useEffect(() => {
    setDiffHtmlIn (dmp.diff_prettyHtml(dmp.diff_main(inputText,  generatedText)));
    setDiffHtmlOut(dmp.diff_prettyHtml(dmp.diff_main(generatedText, latestFinal)));
  }, [inputText, generatedText, latestFinal]);

  const inputWords     = wordCount(inputText);
  const generatedWords = wordCount(generatedText);
  const finalWords     = wordCount(latestFinal);

  /* ------------------------------------------------------------------ */
  /* 4.  Survey form state                                              */
  /* ------------------------------------------------------------------ */
  const [answers, setAnswers] = useState({
    needs: null, easy: null, meaning: null, relevancy: null,
    grammar: null, guidelines: null, clarity: null, coherent: null,
    editing_effort: null,
    main_reasons: "", additional_comments: "",
  });

  const handleRadio = e =>
    setAnswers(p => ({ ...p, [e.target.name]: p[e.target.name] === e.target.value ? null : e.target.value }));

  const handleText  = e =>
    setAnswers(p => ({ ...p, [e.target.name]: e.target.value }));

  const handleSubmit = async e => {
    e.preventDefault();
    await fetch("/api/simplifications/survey", {
      method : "PUT",
      headers: { "Content-Type": "application/json" },
      body   : JSON.stringify({ email, inputText, responses: answers })
    });
    setSubmitted(true);
  };

  /* ------------------------------------------------------------------ */
  /* 5.  Versionâ€‘history sideâ€‘bar                                       */
  /* ------------------------------------------------------------------ */
  const pickVersion = idx => {
    setLatestFinal(saveHistory[idx].finalText);
    setSelectedVersion(idx + 1);
  };

  /* ------------------------------------------------------------------ */
  /* 6.  Earlyâ€‘exit if routerâ€‘state missing (e.g. user refreshed page)  */
  /* ------------------------------------------------------------------ */
  if (!email) return <p style={{ padding: 40 }}>No session data found. Please restart a simplification.</p>;

  /* ------------------------------------------------------------------ */
  /* 7.  Render                                                         */
  /* ------------------------------------------------------------------ */
  return (
    <>
      {/* â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€  NAVBAR  â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */}
      <nav className={styles.navbar}>
        <h1 onClick={() => navigate("/")} style={{ cursor: "pointer" }}>TextÂ SimplificationÂ Tool</h1>
        <button className={styles.white_btn} onClick={() => { localStorage.removeItem("token"); navigate("/Login"); }}>
          Logout
        </button>
      </nav>

      <div className={styles.container}>
        {/* â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€  HISTORY SIDEBAR  â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */}
        <div className={`${styles.sidebar} ${sidebar ? styles.expanded : ""}`}>
          <button className={styles.historyIcon} onClick={() => setSidebar(s => !s)}>
            ðŸ•’ <p style={{ fontSize: 15 }}>History</p>
          </button>

          {sidebar && (
            <div className={styles.historyContent}>
              <button className={styles.closeButton} onClick={() => setSidebar(false)}>âœ–</button>
              <ul className={styles.historyList}>
                {saveHistory.map((v, i) => (
                  <li key={i}
                      className={`${styles.historyItem} ${selectedVersion === i + 1 ? styles.activeVersion : ""}`}
                      onClick={() => pickVersion(i)}>
                    VersionÂ {i + 1}
                  </li>
                ))}
              </ul>
            </div>
          )}
        </div>

        {/* â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€  MAIN CONTENT  â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */}
        <div className={`${styles.mainContent} ${sidebar ? styles.withSidebar : ""}`}>
          {!submitted ? (
            <>
              {/* â”€â”€â”€â”€â”€  Triple textâ€‘boxes  â”€â”€â”€â”€â”€ */}
              <div className={styles.textareas_container}>
                {/* Input */}
                <TextBox
                  label="Input Text"
                  value={inputText}
                  words={inputWords}
                  onCopy={() => navigator.clipboard.writeText(inputText)}
                  filename="InputText"
                />

                {/* AI gen */}
                <TextBox
                  label="AIâ€‘generated Text"
                  value={generatedText}
                  words={generatedWords}
                  onCopy={() => navigator.clipboard.writeText(generatedText)}
                  filename="GeneratedText"
                  toggleDiff={{
                    shown : showDiffIn,
                    label : showDiffIn ? "Hide comparison" : "Compare with input",
                    onClick: () => setShowDiffIn(s => !s)
                  }}
                >
                  {showDiffIn && <DiffBox html={diffHtmlIn} title="InputÂ vsÂ AIâ€‘generated" />}
                </TextBox>

                {/* Final */}
                <TextBox
                  label="FinalÂ Revised Text"
                  value={latestFinal}
                  words={finalWords}
                  onCopy={() => navigator.clipboard.writeText(latestFinal)}
                  filename="FinalText"
                  toggleDiff={{
                    shown : showDiffOut,
                    label : showDiffOut ? "Hide comparison" : "Compare with AIâ€‘generated",
                    onClick: () => setShowDiffOut(s => !s)
                  }}
                >
                  {showDiffOut && <DiffBox html={diffHtmlOut} title="AIâ€‘generatedÂ vsÂ Final" />}
                </TextBox>
              </div>

              {/* â”€â”€â”€â”€â”€  Survey Form  â”€â”€â”€â”€â”€ */}
              <SurveyForm
                answers={answers}
                onRadio={handleRadio}
                onText ={handleText}
                onSubmit={handleSubmit}
              />
            </>
          ) : (
            <ThankYou onRestart={() => navigate("/simplify", { replace: true })} />
          )}
        </div>
      </div>
    </>
  );
}

/* ------------------------------------------------------------------ */
/* Helper subâ€‘components                                              */
/* ------------------------------------------------------------------ */
function TextBox({ label, value, words, onCopy, filename, toggleDiff, children }) {
  return (
    <div className={styles.text_container}>
      <div className={styles.labelWrapper}>
        <label className={styles.label}>{label}</label>
        <div className={styles.actions}>
          <span className={styles.copyIcon} onClick={onCopy}>ðŸ“‹</span>
          <span className={styles.copyIcon}
                onClick={() => saveAs(new Blob([value], { type: "text/plain;charset=utf-8" }), `${filename}.txt`)}>
            ðŸ“¥
          </span>
          {toggleDiff && (
            <button className={styles.toggleDiffBtn} onClick={toggleDiff.onClick}>
              {toggleDiff.label}
            </button>
          )}
        </div>
      </div>
      <p className={styles.wordCount}>Words: {words}</p>
      <textarea readOnly value={value} className={`${styles.output_box} ${styles.side_by_side}`} />
      {children}
    </div>
  );
}

const DiffBox = ({ html, title }) => (
  <div className={styles.diff_box}>
    <label className={styles.label}>{title}</label>
    <div className={styles.diff_output} dangerouslySetInnerHTML={{ __html: html }} />
  </div>
);

function SurveyForm({ answers, onRadio, onText, onSubmit }) {
  return (
    <form className={styles.surveyForm} onSubmit={onSubmit}>
      <h2>Survey</h2>
      <h3>Your feedback is valuable (all questions optional).</h3>

      {/* repeat blocks like below for each Likert question â€¦ */}
      <Likert name="needs" label="Does the AIâ€‘generated text meet your needs?" onChange={onRadio} />

      {/* freeâ€‘text */}
      <div className={styles.surveyQuestion}>
        <label>Main reasons for editing (optional):</label>
        <textarea name="main_reasons" rows="3" value={answers.main_reasons} onChange={onText} />
      </div>

      <button className={styles.submitButton} type="submit">Submit</button>
    </form>
  );
}

const Likert = ({ name, label, onChange }) => (
  <div className={styles.surveyQuestion}>
    <label>{label}</label>
    <div className={styles.surveyOptions}>
      {["1","2","3","4","5"].map(v => (
        <label key={v}><input type="radio" name={name} value={v} onChange={onChange} />Â {v}</label>
      ))}
    </div>
  </div>
);

const ThankYou = ({ onRestart }) => (
  <div className={styles.thankYouContainer}>
    <h2>ThankÂ you!</h2>
    <p>Your responses have been saved.</p>
    <button className={styles.redirectButton} onClick={onRestart}>
      Start another simplification
    </button>
  </div>
);
