import { useState } from "react";
import styles from "./styles.module.css";
import * as pdfjsLib from "pdfjs-dist/legacy/build/pdf";
import jwtDecode from "jwt-decode";

pdfjsLib.GlobalWorkerOptions.workerSrc = `${process.env.PUBLIC_URL}/pdf.worker.mjs`;

const Main = () => {
	const [inputText, setInputText] = useState("");
	const [outputText, setOutputText] = useState("");
	const [isLoading, setIsLoading] = useState(false);
	const [isSubmitted, setIsSubmitted] = useState(false);
	const [uploadedFileName, setUploadedFileName] = useState(""); // State for file name

	const handleLogout = () => {
		localStorage.removeItem("token");
		window.location.reload();
	};

	const handleSubmit = async () => {
		if (!inputText.trim()) return;
		setIsLoading(true);

		try {
			const prompt = `Simplify the following text for better readability while preserving meaning:\n\n"${inputText}"`;

			const response = await fetch("http://localhost:5001/api/gpt4", {
				method: "POST",
				headers: { "Content-Type": "application/json" },
				body: JSON.stringify({ prompt }),
			});

			const data = await response.json();
			const cleanedResponse = data?.response?.replace(/^"|"$/g, "") || "No response received.";
			setOutputText(cleanedResponse);
			setIsSubmitted(true);
		} catch (error) {
			console.error("Error fetching GPT-4 response:", error);
			setOutputText("An error occurred while fetching the response.");
		}

		setIsLoading(false);
	};

	// const handleSave = () => {
	// 	alert("Output saved!");
	// };


	const handleSave = async () => {
	  try {
		const token = localStorage.getItem("token"); // Retrieve the token from localStorage
		if (!token) {
		  alert("User not logged in!");
		  return;
		}

		// Decode the token to get user info
		const decoded = jwtDecode(token); // Decode the JWT
		const userId = decoded._id; // Extract userId
		const email = decoded.email; // Extract email

		const payload = {
		  userId,
		  inputText,
		  outputText,
		};

		const response = await fetch("http://localhost:8080/api/simplifications", {
		  method: "POST",
		  headers: { "Content-Type": "application/json" },
		  body: JSON.stringify(payload),
		});

		if (response.ok) {
		  const data = await response.json();
		  alert("Simplification saved successfully!");
		  console.log(data);
		} else {
		  const error = await response.json();
		  alert(error.message || "Failed to save simplification.");
		}
	  } catch (error) {
		console.error("Error saving simplification:", error);
		alert("An error occurred. Please try again.");
	  }
	};



	const handleFileUpload = async (event) => {
		const file = event.target.files[0];
		if (!file) return;

		try {
			setUploadedFileName(file.name); // Set the uploaded file name
			const fileReader = new FileReader();
			fileReader.onload = async function () {
				const typedArray = new Uint8Array(this.result);

				const pdf = await pdfjsLib.getDocument(typedArray).promise;
				let text = "";

				// Extract text from all pages
				for (let i = 1; i <= pdf.numPages; i++) {
					const page = await pdf.getPage(i);
					const textContent = await page.getTextContent();
					text += textContent.items.map((item) => item.str).join(" ");
				}

				setInputText(text);
			};
			fileReader.readAsArrayBuffer(file);
		} catch (error) {
			console.error("Error reading PDF:", error);
			alert("Failed to extract text from the PDF.");
		}
	};

	return (
		<>
			{/* Navbar */}
			<nav className={styles.navbar}>
				<h1>Text Simplification Study</h1>
				<button className={styles.white_btn} onClick={handleLogout}>
					Logout
				</button>
			</nav>

			{/* Main Content */}
			<div className={styles.main_container}>
				{/* Conditional Rendering for Layout */}
				{!isSubmitted ? (
					<div className={styles.form_container}>
						<div className={styles.input_area}>
							{/* Full-width Input Box */}
							<textarea
								className={styles.textarea}
								placeholder="Original Text"
								value={inputText}
								onChange={(e) => setInputText(e.target.value)}
							></textarea>

							{/* File Upload */}
							{/* Drag and Drop Upload Area */}
							<div className={styles.upload_area}>
								<label htmlFor="fileUpload" className={styles.upload_box}>
									{uploadedFileName ? (
										<>File uploaded: <strong>{uploadedFileName}</strong></>
									) : (
										<>
											Click to Upload a PDF
											<br />
											<span>...or drag and drop a file.</span>
										</>
									)}
								</label>
								<input
									type="file"
									id="fileUpload"
									accept="application/pdf"
									onChange={handleFileUpload}
									className={styles.hidden_input}
								/>
							</div>
						</div>

						{/* Centered Submit Button */}
						<button
							className={styles.submit_btn}
							onClick={handleSubmit}
							disabled={!inputText.trim() || isLoading}
						>
							{isLoading ? "Processing..." : "Submit"}
						</button>
					</div>
				) : (
					<div className={styles.form_container}>
						{/* Side-by-Side Input and Output */}
						<div className={styles.textareas_container}>
							<textarea
								className={`${styles.textarea} ${styles.side_by_side}`}
								placeholder="Original Text"
								value={inputText}
								onChange={(e) => setInputText(e.target.value)}
							></textarea>

							<textarea
								className={`${styles.output_box} ${styles.side_by_side}`}
								value={outputText}
								onChange={(e) => setOutputText(e.target.value)} // Allow editing
								placeholder="Output"
							></textarea>
						</div>

						{/* Centered Save Button */}
						<button
							className={styles.submit_btn}
							onClick={handleSave}
							disabled={isLoading}
						>
							Save
						</button>
					</div>
				)}
			</div>
		</>
	);
};

