import React from "react";
import { useState, useEffect, useRef } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import DiffMatchPatch from "diff-match-patch";
import styles from "./styles.module.css";

const Review = () => {
  const { state } = useLocation();
  const { inputText, outputText: initialOutputText } = state || {
    inputText: "",
    outputText: "",
  };
  const [outputText, setOutputText] = useState(initialOutputText);
  const [diffHtml, setDiffHtml] = useState(""); // State for storing diff HTML
  const [isEditable, setIsEditable] = useState(false);
  const navigate = useNavigate();

  const contentEditableRef = useRef(null); // Reference to the contentEditable div

  const [isLoading, setIsLoading] = useState(false);
  const [isSaveButtonVisible, setIsSaveButtonVisible] = useState(true);
  const [showSurveyPrompt, setShowSurveyPrompt] = useState(false); // State for survey prompt

  // Function to calculate and render diff
  const generateDiff = (input, output) => {
    const dmp = new DiffMatchPatch();
    const diffs = dmp.diff_main(input, output);
    dmp.diff_cleanupSemantic(diffs); // Optional cleanup for better readability
    return dmp.diff_prettyHtml(diffs); // Generate diff HTML
  };

  const preserveCursorPosition = (editableDiv, callback) => {
    const selection = window.getSelection();
    const range = selection.getRangeAt(0); // Save the current cursor position
    const startOffset = range.startOffset;

    callback(); // Perform the callback (e.g., set the state and update diff)

    // Restore the cursor position
    setTimeout(() => {
      const newRange = document.createRange();
      const targetNode = editableDiv.childNodes[0];
      newRange.setStart(targetNode, Math.min(startOffset, targetNode.length));
      newRange.collapse(true);
      selection.removeAllRanges();
      selection.addRange(newRange);
    }, 0);
  };

  const handleInput = (e) => {
    const editableDiv = contentEditableRef.current;
    preserveCursorPosition(editableDiv, () => {
      const updatedText = editableDiv.textContent; // Get updated text
      setOutputText(updatedText); // Update output text state
      const updatedDiff = generateDiff(inputText, updatedText); // Recalculate diff
      setDiffHtml(updatedDiff); // Update diff HTML
    });
  };

  const handleSave = async (isAuto = false) => {
    try {
      const user = JSON.parse(localStorage.getItem("user")); // Retrieve user info
      if (!user) {
        alert("User not logged in!");
        return;
      }

      const payload = {
        userId: user._id,
        email: user.email,
        inputText,
        ...(isEditable
          ? { editedText: outputText }
          : { outputText, editableText: outputText }),
      };

      const endpoint = isEditable
        ? "http://localhost:8080/api/simplifications/edit" // Endpoint for updating
        : "http://localhost:8080/api/simplifications"; // Endpoint for initial save

      const method = isEditable ? "PUT" : "POST"; // Use PUT for updates, POST for saves

      const response = await fetch(endpoint, {
        method,
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
      });

      if (response.ok) {
        alert(
          isEditable
            ? "Edited text saved successfully!"
            : "Simplification saved successfully!"
        );
        setIsEditable(false); // Exit editing mode after saving edits
      } else {
        const error = await response.json();
        alert(error.message || "Failed to save simplification.");
      }
    } catch (error) {
      console.error("Error saving simplification:", error);
      alert("An error occurred. Please try again.");
    }
  };

  return (
    <>
      <nav className={styles.navbar}>
        <h1>Text Simplification Study</h1>
        <button className={styles.white_btn} onClick={() => navigate("/")}>
          Logout
        </button>
      </nav>

      <div className={styles.main_container}>
        <div className={styles.description}>
          <p>
            Review the generated output and make any necessary edits. Changes
            are highlighted in real-time for easy visualization.
          </p>

          <div className={styles.form_container}>
            {/* Input and Diff Boxes */}
            <div className={styles.textareas_container}>
              {/* Input Text Box */}
              <div className={styles.text_container}>
                <label className={styles.label} htmlFor="inputText">
                  Input Text
                </label>
                <textarea
                  id="inputText"
                  className={`${styles.textarea} ${styles.side_by_side}`}
                  value={inputText}
                  readOnly
                ></textarea>
              </div>

              {/* Output Text Box */}
              <div className={styles.text_container}>
                <label className={styles.label} htmlFor="outputText">
                  System-generated Text
                </label>
                {isEditable ? (
                  <div
                    id="outputText"
                    ref={contentEditableRef}
                    className={`${styles.output_box}`}
                    contentEditable={true}
                    onInput={handleInput}
                    dangerouslySetInnerHTML={{ __html: diffHtml }}
                  ></div>
                ) : (
                  <div
                    id="diffText"
                    className={`${styles.output_box}`}
                    dangerouslySetInnerHTML={{ __html: diffHtml }}
                  ></div>
                )}
              </div>
            </div>

            {/* Save and Edit Buttons */}
            <div className={styles.button_container}>
              {isSaveButtonVisible && (
                <button
                  className={`${styles.save_btn} ${styles.text_btn}`}
                  onClick={() => handleSave(false)}
                  disabled={isLoading}
                >
                  Save
                </button>
              )}
              {!isEditable && (
                <button
                  className={`${styles.edit_btn} ${styles.text_btn}`}
                  onClick={() => setIsEditable(true)}
                >
                  Edit
                </button>
              )}
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Review;

