import { useState } from "react";
import styles from "./styles.module.css";

const Main = () => {
	const [inputText, setInputText] = useState("");
	const [outputText, setOutputText] = useState("");
	const [isLoading, setIsLoading] = useState(false);
	const [isSubmitted, setIsSubmitted] = useState(false);

	const handleLogout = () => {
		localStorage.removeItem("token");
		window.location.reload();
	};

	const handleSubmit = async () => {
		if (!inputText.trim()) return;
		setIsLoading(true);

		try {
			const prompt = `Simplify the following text for better readability while preserving meaning:\n\n"${inputText}"`;

			const response = await fetch("http://localhost:5001/api/gpt4", {
				method: "POST",
				headers: { "Content-Type": "application/json" },
				body: JSON.stringify({ prompt }),
			});

			const data = await response.json();
			const cleanedResponse = data?.response?.replace(/^"|"$/g, '') || "No response received.";
			setOutputText(cleanedResponse);
			setIsSubmitted(true);
		} catch (error) {
			console.error("Error fetching GPT-4 response:", error);
			setOutputText("An error occurred while fetching the response.");
		}

		setIsLoading(false);
	};

	const handleSave = () => {
		alert("Output saved!");
	};

	return (
		<>
			{/* Navbar */}
			<nav className={styles.navbar}>
				<h1>Text Simplification Study</h1>
				<button className={styles.white_btn} onClick={handleLogout}>
					Logout
				</button>
			</nav>

			{/* Main Content */}
			<div className={styles.main_container}>
				{/* Conditional Rendering for Layout */}
				{!isSubmitted ? (
					<div className={styles.form_container}>
						{/* Full-width Input Box */}
						<textarea
							className={styles.textarea}
							placeholder="Original Text"
							value={inputText}
							onChange={(e) => setInputText(e.target.value)}
						></textarea>

						{/* Centered Submit Button */}
						<button
							className={styles.submit_btn}
							onClick={handleSubmit}
							disabled={isLoading}
						>
							{isLoading ? "Processing..." : "Submit"}
						</button>
					</div>
				) : (
					<div className={styles.form_container}>
						{/* Side-by-Side Input and Output */}
						<div className={styles.textareas_container}>
							<textarea
								className={`${styles.textarea} ${styles.side_by_side}`}
								placeholder="Original Text"
								value={inputText}
								onChange={(e) => setInputText(e.target.value)}
							></textarea>

							<textarea
								className={`${styles.output_box} ${styles.side_by_side}`}
								value={outputText}
								onChange={(e) => setOutputText(e.target.value)} // Allow editing
								placeholder="Output"
							></textarea>
						</div>

						{/* Centered Save Button */}
						<button
							className={styles.submit_btn}
							onClick={handleSave}
							disabled={isLoading}
						>
							Save
						</button>
					</div>
				)}
			</div>
		</>
	);
};

export default Main;


