		try {
		  const user = JSON.parse(localStorage.getItem("user")); // Retrieve user info
		  console.log("User info from localStorage:", user);
		  if (!user) {
			alert("User not logged in!");
			return;
		  }

		  const payload = {
			userId: user._id,
			email: user.email,
			inputText,
			outputText,
		  };

		  const response = await fetch("http://localhost:8080/api/simplifications", {
			method: "POST",
			headers: { "Content-Type": "application/json" },
			body: JSON.stringify(payload),
		  });

		  if (response.ok) {
			const data = await response.json();
			alert("Simplification saved successfully!");
		  } else {
			const error = await response.json();
			alert(error.message || "Failed to save simplification.");
		  }
		} catch (error) {
		  console.error("Error saving simplification:", error);
		  alert("An error occurred. Please try again.");
		}
	  };




	const handleFileUpload = async (event) => {
		const file = event.target.files[0];
		if (!file) return;

		try {
			setUploadedFileName(file.name); // Set the uploaded file name
			const fileReader = new FileReader();
			fileReader.onload = async function () {
				const typedArray = new Uint8Array(this.result);

				const pdf = await pdfjsLib.getDocument(typedArray).promise;
				let text = "";

				// Extract text from all pages
				for (let i = 1; i <= pdf.numPages; i++) {
					const page = await pdf.getPage(i);
					const textContent = await page.getTextContent();
					text += textContent.items.map((item) => item.str).join(" ");
				}

				setInputText(text);
			};
			fileReader.readAsArrayBuffer(file);
		} catch (error) {
			console.error("Error reading PDF:", error);
			alert("Failed to extract text from the PDF.");
		}
	};

	return (
		<>
