import React from "react";
import { useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import styles from "./styles.module.css";

const Review = () => {
  const { state } = useLocation();
  const { inputText, outputText } = state || { inputText: "", outputText: "" };
//   const navigate = useNavigate();

  	// const [inputText, setInputText] = useState("");
	// const [outputText, setOutputText] = useState("");
	const [isLoading, setIsLoading] = useState(false);
	const [isSubmitted, setIsSubmitted] = useState(false);
	const [isEditable, setIsEditable] = useState(false); // For toggling edit mode
	const [uploadedFileName, setUploadedFileName] = useState(""); // State for file name
	const [isSaveButtonVisible, setIsSaveButtonVisible] = useState(true);
	const [showSurveyPrompt, setShowSurveyPrompt] = useState(false); // State for survey prompt
    const navigate = useNavigate();

	const handleLogout = () => {
	  localStorage.removeItem("token");
	  window.location.reload();
	};

//   const handleSave = () => {
//     alert("Text saved successfully!");
//     // Add your save logic here if needed
//     navigate("/simplify"); // Redirect back to Main
//   };

//   const handleEdit = () => {
//     navigate("/simplify", { state: { inputText, outputText } });
//   };

  	const handleSave = async (isAuto = false) => {
//		setIsSaveButtonVisible(false);
if (isAuto && isAuto.nativeEvent) {
	isAuto = false; // Default behavior if called by an event
}

//console.log("isAuto:", isAuto);
		//console.log("isSaveButtonVisible", isSaveButtonVisible);


		try {
		  const user = JSON.parse(localStorage.getItem("user")); // Retrieve user info
		  if (!user) {
			alert("User not logged in!");
			return;
		  }

		  const payload = {
			userId: user._id,
			email: user.email,
			inputText,
			...(isEditable ? { editedText: outputText } : { outputText, editableText: outputText }),
		  };

		  const endpoint = isEditable
			? "http://localhost:8080/api/simplifications/edit" // Endpoint for updating
			: "http://localhost:8080/api/simplifications"; // Endpoint for initial save

		  const method = isEditable ? "PUT" : "POST"; // Use PUT for updates, POST for saves

		  const response = await fetch(endpoint, {
			method,
			headers: { "Content-Type": "application/json" },
			body: JSON.stringify(payload),
		  });

		  if (response.ok) {
			const data = await response.json();
			alert(isEditable ? "Edited text saved successfully!" : "Simplification saved successfully!");

			//setShowSurveyPrompt(true); 
			console.log("isSaveButtonVisible", isSaveButtonVisible);
			console.log("isEditable", isEditable);
			console.log("isAuto", isAuto);
			if (!isSaveButtonVisible) {  //edit, save, edit
				setShowSurveyPrompt(false); // Show the survey prompt
			  }
			else if (isSaveButtonVisible && !isEditable && isAuto)  //true false -- first time save or edit button clicked
			{
				setShowSurveyPrompt(false);
			}
			else if (isSaveButtonVisible && isEditable)
			{
				setShowSurveyPrompt(true);
			} 
			else if (isSaveButtonVisible && !isEditable && !isAuto)  //true false -- first time save or edit button clicked
			{
				setShowSurveyPrompt(true);
			}


			if (isEditable) {
			 {
				setIsEditable(false); // Exit editing mode after saving edits
			  // disable saving button
			  setIsSaveButtonVisible(false); // Hide the save button
			   // Show the survey prompt
			 // setShowSurveyPrompt(true); 
			 }
		  }} else {
			const error = await response.json();
			alert(error.message || "Failed to save simplification.");
		  }
		} catch (error) {
		  console.error("Error saving simplification:", error);
		  alert("An error occurred. Please try again.");
		}

		//setShowSurveyPrompt(true);
		// Page for survey (only save -- 2 boxes, else edit+save -- 3 boxes) -- fetch inputText, outputText and editedText. If outputText is same as editedText, then show only 2 text boxes, else 3 text boxes (all not editable) with 5 questions below.
		        // Navigate to survey page

		//navigate("/survey", { state: { email: user.email, inputText } });
		//isAuto = false;
		//navigate("/save"); // Navigate to save page
	  };



	const handleEdit = () => {
	  let isAuto = true;
	  handleSave(isAuto); // Save the current output text
	  setIsEditable(true); // Enable editing mode
	  setIsSaveButtonVisible(true);

	  //navigate("/edit"); // Navigate to edit page

	  //setShowSurveyPrompt(false);

	  // Add back button
	};

  return (
    <>
    <nav className={styles.navbar}>
      <h1>Text Simplification Study</h1>
      <button className={styles.white_btn} onClick={handleLogout}>
        Logout
      </button>
    </nav>

    <div className={styles.main_container}>
     			<div className={styles.form_container}>
     			  {/* Input and Output Boxes */}
     			  <div className={styles.textareas_container}>
     				{/* Input Text Box */}
     				<div className={styles.text_container}>
     				  <label className={styles.label} htmlFor="inputText">
     					Input Text
     				  </label>
    				  <textarea
    					id="inputText"
    					className={`${styles.textarea} ${styles.side_by_side}`}
    					value={inputText}
    					//onChange={(e) => setInputText(e.target.value)}
    					readOnly
    				  ></textarea>
    				</div>

    				{/* Output Text Box */}
    				<div className={styles.text_container}>
    				  <label className={styles.label} htmlFor="outputText">
    					System-generated Text
    				  </label>
    				  <textarea
    					id="outputText"
    					className={`${styles.output_box} ${styles.side_by_side}`}
    					value={outputText}
    					//onChange={(e) => setOutputText(e.target.value)}
    					readOnly={!isEditable}
    					placeholder="Output"
    				  ></textarea>
    				</div>
    			  </div>


    				{/* Save and Edit Buttons */}
    				<div className={styles.button_container}>


    				{isSaveButtonVisible && (
    				<button
    					className={`${styles.save_btn} ${styles.text_btn}`}
    					onClick={handleSave}
    					disabled={isLoading}
    				>
    					Save
    				</button>
    				)}

    				{!isEditable && (
    					<button
    					className={`${styles.edit_btn} ${styles.text_btn}`}
    					onClick={handleEdit}
    					>
    					Edit
    					</button>
    				)}
    				</div>
          {/* Survey Prompt */}
    	  {showSurveyPrompt && (
                  <div className={styles.survey_prompt}>
                    <p>
                      Please take the survey to help us improve {" "}
                      <button
                        className={styles.link_btn}
                        onClick={() =>
                          navigate("/survey", {
                            state: { email: JSON.parse(localStorage.getItem("user")).email, inputText },
                          })
                        }
                      >
                        Take the Survey
                      </button>
                    </p>
                  </div>
                )}

    			</div>

    		</div>
    	  </>
    // <div className={styles.review_container}>
    //   <h1>Review Simplified Text</h1>
    //   <div className={styles.textareas_container}>
    //     <div className={styles.text_container}>
    //       <label className={styles.label}>Input Text</label>
    //       <textarea
    //         className={`${styles.textarea} ${styles.readonly}`}
    //         value={inputText}
    //         readOnly
    //       ></textarea>
    //     </div>

    //     <div className={styles.text_container}>
    //       <label className={styles.label}>Simplified Text</label>
    //       <textarea
    //         className={`${styles.textarea} ${styles.readonly}`}
    //         value={outputText}
    //         readOnly
    //       ></textarea>
    //     </div>
    //   </div>

    //   <div className={styles.button_container}>
    //     <button className={styles.save_btn} onClick={handleSave}>
    //       Save
    //     </button>
    //     <button className={styles.edit_btn} onClick={handleEdit}>
    //       Edit
    //     </button>
    //   </div>
    // </div>
    // </>
  );
};

export default Review;


