import { useState } from "react";
import { useNavigate } from "react-router-dom";
import * as pdfjsLib from "pdfjs-dist/legacy/build/pdf";
import styles from "./styles.module.css";

pdfjsLib.GlobalWorkerOptions.workerSrc = `${process.env.PUBLIC_URL}/pdf.worker.mjs`;

const Main = () => {
  const [inputText, setInputText] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [uploadedFileName, setUploadedFileName] = useState("");
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem("token");
    window.location.reload();
  };

  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    try {
      setUploadedFileName(file.name);
      const fileReader = new FileReader();
      fileReader.onload = async function () {
        const typedArray = new Uint8Array(this.result);
        const pdf = await pdfjsLib.getDocument(typedArray).promise;
        let text = "";

        for (let i = 1; i <= pdf.numPages; i++) {
          const page = await pdf.getPage(i);
          const textContent = await page.getTextContent();
          text += textContent.items.map((item) => item.str).join(" ");
        }

        setInputText(text);
      };
      fileReader.readAsArrayBuffer(file);
    } catch (error) {
      console.error("Error reading PDF:", error);
      alert("Failed to extract text from the PDF.");
    }
  };

  const handleSubmit = async () => {
    if (!inputText.trim()) return;
    setIsLoading(true);

    try {
      const prompt = `Simplify the following text for better readability while preserving meaning:\n\n"${inputText}"`;

      const response = await fetch("http://localhost:5001/api/gpt4", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ prompt }),
      });

      const data = await response.json();
      const cleanedResponse = data?.response?.replace(/^"|"$/g, "") || "No response received.";

      navigate("/review", { state: { inputText, outputText: cleanedResponse } });
    } catch (error) {
      console.error("Error fetching GPT-4 response:", error);
      alert("An error occurred while simplifying the text.");
    }

    setIsLoading(false);
  };

  return (
    <>
      <nav className={styles.navbar}>
        <h1>Text Simplification Study</h1>
        <button className={styles.white_btn} onClick={handleLogout}>
          Logout
        </button>
      </nav>

      <div className={styles.main_container}>
        <div className={styles.description}>
          <p>
            This app is about simplifying text for improved readability and accessibility.
          </p>
          <p>
            Upload a document or paste your text to get a simplified version with just a click.
          </p>

          <div className={styles.form_container}>
            <div className={styles.input_area}>
              <label className={styles.label} htmlFor="inputText">
                Input Text
              </label>
              <textarea
                id="inputText"
                className={styles.textarea}
                placeholder="Original Text"
                value={inputText}
                onChange={(e) => setInputText(e.target.value)}
              ></textarea>
            </div>

            <div className={styles.or_divider}>OR</div>

            <div className={styles.upload_area}>
              <label htmlFor="fileUpload" className={styles.upload_box}>
                {uploadedFileName ? `File uploaded: ${uploadedFileName}` : "Click to Upload a PDF"}
              </label>
              <input
                type="file"
                id="fileUpload"
                accept="application/pdf"
                onChange={handleFileUpload}
                className={styles.hidden_input}
              />
            </div>
          </div>

          <button
            className={styles.submit_btn}
            onClick={handleSubmit}
            disabled={!inputText.trim() || isLoading}
          >
            {isLoading ? "Processing..." : "Simplify Text"}
          </button>
        </div>
      </div>
    </>
  );
};

export default Main;

