import React from "react";
import { useLocation, useNavigate } from "react-router-dom";
import styles from "./styles.module.css";

const Review = () => {
  const { state } = useLocation();
  const { inputText, outputText } = state || { inputText: "", outputText: "" };
//   const navigate = useNavigate();

  	// const [inputText, setInputText] = useState("");
	// const [outputText, setOutputText] = useState("");
	const [isLoading, setIsLoading] = useState(false);
	const [isSubmitted, setIsSubmitted] = useState(false);
	const [isEditable, setIsEditable] = useState(false); // For toggling edit mode
	const [uploadedFileName, setUploadedFileName] = useState(""); // State for file name
	const [isSaveButtonVisible, setIsSaveButtonVisible] = useState(true);
	const [showSurveyPrompt, setShowSurveyPrompt] = useState(false); // State for survey prompt
    const navigate = useNavigate();

	const handleLogout = () => {
	  localStorage.removeItem("token");
	  window.location.reload();
	};

  const handleSave = () => {
    alert("Text saved successfully!");
    // Add your save logic here if needed
    navigate("/simplify"); // Redirect back to Main
  };

  const handleEdit = () => {
    navigate("/simplify", { state: { inputText, outputText } });
  };

  return (
    <>
    <nav className={styles.navbar}>
      <h1>Text Simplification Study</h1>
      <button className={styles.white_btn} onClick={handleLogout}>
        Logout
      </button>
    </nav>

    <div className={styles.main_container}>
     			<div className={styles.form_container}>
     			  {/* Input and Output Boxes */}
     			  <div className={styles.textareas_container}>
     				{/* Input Text Box */}
     				<div className={styles.text_container}>
     				  <label className={styles.label} htmlFor="inputText">
     					Input Text
     				  </label>
    				  <textarea
    					id="inputText"
    					className={`${styles.textarea} ${styles.side_by_side}`}
    					value={inputText}
    					onChange={(e) => setInputText(e.target.value)}
    					readOnly
    				  ></textarea>
    				</div>

    				{/* Output Text Box */}
    				<div className={styles.text_container}>
    				  <label className={styles.label} htmlFor="outputText">
    					System-generated Text
    				  </label>
    				  <textarea
    					id="outputText"
    					className={`${styles.output_box} ${styles.side_by_side}`}
    					value={outputText}
    					onChange={(e) => setOutputText(e.target.value)}
    					readOnly={!isEditable}
    					placeholder="Output"
    				  ></textarea>
    				</div>
    			  </div>


    				{/* Save and Edit Buttons */}
    				<div className={styles.button_container}>
    				{/* <button
    					className={`${styles.save_btn} ${styles.text_btn}`}
    					onClick={handleSave}
    				>
    					Save
    				</button> */}

    				{isSaveButtonVisible && (
    				<button
    					className={`${styles.save_btn} ${styles.text_btn}`}
    					onClick={handleSave}
    					disabled={isLoading}
    				>
    					Save
    				</button>
    				)}

    				{!isEditable && (
    					<button
    					className={`${styles.edit_btn} ${styles.text_btn}`}
    					onClick={handleEdit}
    					>
    					Edit
    					</button>
    				)}
    				</div>
          {/* Survey Prompt */}
    	  {showSurveyPrompt && (
                  <div className={styles.survey_prompt}>
                    <p>
                      Please take the survey to help us improve.{" "}
                      <button
                        className={styles.link_btn}
                        onClick={() =>
                          navigate("/survey", {
                            state: { email: JSON.parse(localStorage.getItem("user")).email, inputText },
                          })
                        }
                      >
                        Take the Survey
                      </button>
                    </p>
                  </div>
                )}

    			</div>
    		  }
    		</div>
    	  </>
    // <div className={styles.review_container}>
    //   <h1>Review Simplified Text</h1>
    //   <div className={styles.textareas_container}>
    //     <div className={styles.text_container}>
    //       <label className={styles.label}>Input Text</label>
    //       <textarea
    //         className={`${styles.textarea} ${styles.readonly}`}
    //         value={inputText}
    //         readOnly
    //       ></textarea>
    //     </div>

    //     <div className={styles.text_container}>
    //       <label className={styles.label}>Simplified Text</label>
    //       <textarea
    //         className={`${styles.textarea} ${styles.readonly}`}
    //         value={outputText}
    //         readOnly
    //       ></textarea>
    //     </div>
    //   </div>

    //   <div className={styles.button_container}>
    //     <button className={styles.save_btn} onClick={handleSave}>
    //       Save
    //     </button>
    //     <button className={styles.edit_btn} onClick={handleEdit}>
    //       Edit
    //     </button>
    //   </div>
    // </div>
    // </>
  );
};

export default Review;


