import { useState } from "react";
import styles from "./styles.module.css";

const Main = () => {
    const [inputText, setInputText] = useState("");
    const [outputText, setOutputText] = useState("");
    const [isLoading, setIsLoading] = useState(false);
    const [buttonText, setButtonText] = useState("Submit");

    const handleLogout = () => {
        localStorage.removeItem("token");
        window.location.reload();
    };

    const handleSubmit = async () => {
        if (!inputText.trim()) return;
        setIsLoading(true);

        try {
            // Define the prompt to send with the user input
            const prompt = `Simplify the following text for better readability while preserving meaning:\n\n"${inputText}"`;

            const response = await fetch("http://localhost:5001/api/gpt4", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ prompt }),
            });

            const data = await response.json();
            const cleanedResponse = data?.response?.replace(/^"|"$/g, '') || "No response received.";
            setOutputText(cleanedResponse);

            // Change button text to "Save"
            setButtonText("Save");
        } catch (error) {
            console.error("Error fetching GPT-4 response:", error);
            setOutputText("An error occurred while fetching the response.");
        }

        setIsLoading(false);
    };

    return (
        <div className={styles.main_container}>
            <nav className={styles.navbar}>
                <h1>Text Simplification Study</h1>
                <button className={styles.white_btn} onClick={handleLogout}>
                    Logout
                </button>
            </nav>

            <div className={styles.form_container}>
                <div className={styles.text_areas_container}>
                    {/* Input Textarea */}
                    <textarea
                        className={styles.textarea}
                        placeholder="Original Text"
                        value={inputText}
                        onChange={(e) => setInputText(e.target.value)}
                    ></textarea>

                    {/* Output Textarea */}
                    <textarea
                        className={styles.textarea}
                        readOnly
                        placeholder="Simplified Text will appear here"
                        value={outputText}
                    ></textarea>
                </div>

                {/* Submit/Save Button */}
                <button
                    className={styles.submit_btn}
                    onClick={handleSubmit}
                    disabled={isLoading}
                >
                    {isLoading ? "Processing..." : buttonText}
                </button>
            </div>
        </div>
    );
};

export default Main;


