import { useState } from "react";
import styles from "./styles.module.css";
import * as pdfjsLib from "pdfjs-dist/legacy/build/pdf";

pdfjsLib.GlobalWorkerOptions.workerSrc = `${process.env.PUBLIC_URL}/pdf.worker.mjs`;

const Main = () => {
	const [inputText, setInputText] = useState("");
	const [outputText, setOutputText] = useState("");
	const [isLoading, setIsLoading] = useState(false);
	const [isSubmitted, setIsSubmitted] = useState(false);
	const [isEditable, setIsEditable] = useState(true); // State for edit/save toggle
	const [uploadedFileName, setUploadedFileName] = useState("");

	const handleLogout = () => {
	localStorage.removeItem("token");
	window.location.reload();
	};

	const handleSubmit = async () => {
	if (!inputText.trim()) return;
	setIsLoading(true);

	try {
		const prompt = `Simplify the following text for better readability while preserving meaning:\n\n"${inputText}"`;

		const response = await fetch("http://localhost:5001/api/gpt4", {
		method: "POST",
		headers: { "Content-Type": "application/json" },
		body: JSON.stringify({ prompt }),
		});

		const data = await response.json();
		const cleanedResponse = data?.response?.replace(/^"|"$/g, "") || "No response received.";
		setOutputText(cleanedResponse);
		setIsSubmitted(true);
	} catch (error) {
		console.error("Error fetching GPT-4 response:", error);
		setOutputText("An error occurred while fetching the response.");
	}

	setIsLoading(false);
	};

	const handleSave = async () => {
	try {
		const user = JSON.parse(localStorage.getItem("user"));
		if (!user) {
		alert("User not logged in!");
		return;
		}

		const payload = {
		userId: user._id,
		email: user.email,
		inputText,
		outputText,
		};

		const response = await fetch("http://localhost:8080/api/simplifications", {
		method: "POST",
		headers: { "Content-Type": "application/json" },
		body: JSON.stringify(payload),
		});

		if (response.ok) {
		alert("Simplification saved successfully!");
		setIsEditable(false); // Make text boxes non-editable
		} else {
		const error = await response.json();
		alert(error.message || "Failed to save simplification.");
		}
	} catch (error) {
		console.error("Error saving simplification:", error);
		alert("An error occurred. Please try again.");
	}
	};

	const handleEdit = () => {
	setIsEditable(true); // Make text boxes editable
	};

	const handleFileUpload = async (event) => {
	const file = event.target.files[0];
	if (!file) return;

	try {
		setUploadedFileName(file.name);
		const fileReader = new FileReader();
		fileReader.onload = async function () {
		const typedArray = new Uint8Array(this.result);

		const pdf = await pdfjsLib.getDocument(typedArray).promise;
		let text = "";

		for (let i = 1; i <= pdf.numPages; i++) {
			const page = await pdf.getPage(i);
			const textContent = await page.getTextContent();
			text += textContent.items.map((item) => item.str).join(" ");
		}

		setInputText(text);
		};
		fileReader.readAsArrayBuffer(file);
	} catch (error) {
		console.error("Error reading PDF:", error);
		alert("Failed to extract text from the PDF.");
	}
	};

	return (
	<>
		{/* Navbar */}
		<nav className={styles.navbar}>
		<h1>Text Simplification Study</h1>
		<button className={styles.white_btn} onClick={handleLogout}>
			Logout
		</button>
		</nav>

		{/* Main Content */}
		<div className={styles.main_container}>
		{!isSubmitted ? (
			<div className={styles.form_container}>

			{/* Input Area */}
			<div className={styles.input_area}>
				<div className={styles.text_container}>
				<label className={styles.label} htmlFor="inputText">
					Input Text
				</label>
				<textarea
					id="inputText"
					className={styles.textarea}
					placeholder="Original Text"
					value={inputText}
					onChange={(e) => setInputText(e.target.value)}
				></textarea>
				</div>

				{/* OR Divider */}
				<div className={styles.or_divider}>OR</div>

				{/* File Upload */}
				<div className={styles.upload_area}>
				<label htmlFor="fileUpload" className={styles.upload_box}>
					{uploadedFileName ? (
					<>
						File uploaded: <strong>{uploadedFileName}</strong>
					</>
					) : (
					<>
						Click to Upload a PDF
						<br />
						<span>...or drag and drop a file.</span>
					</>
					)}
				</label>
				<input
					type="file"
					id="fileUpload"
					accept="application/pdf"
					onChange={handleFileUpload}
					className={styles.hidden_input}
				/>
				</div>
			</div>

			{/* Submit Button */}
			<button
				className={styles.submit_btn}
				onClick={handleSubmit}
				disabled={!inputText.trim() || isLoading}
			>
				{isLoading ? "Processing..." : "Simplify Text"}
			</button>
			</div>
		) : (
			<div className={styles.form_container}>


			{/* Input and Output Boxes */}


			<div className={styles.textareas_container}>
				{/* Input Text Box */}
				<div className={styles.text_container}>
				<label className={styles.label} htmlFor="inputText">
					Input Text
				</label>
				<textarea
					id="inputText"
					className={`${styles.textarea} ${styles.side_by_side}`}
					value={inputText}
					onChange={(e) => setInputText(e.target.value)}
					readOnly
					//readOnly={!isEditable}
				></textarea>
				</div>

				{/* Output Text Box */}
				<div className={styles.text_container}>
				<label className={styles.label} htmlFor="outputText">
					System-generated Text
				</label>
				<textarea
					id="outputText"
					className={`${styles.output_box} ${styles.side_by_side}`}
					value={outputText}
					onChange={(e) => setOutputText(e.target.value)}
					readOnly={!isEditable}
					placeholder="Output"
				></textarea>


				</div>
			</div>

			{/* Toggle Between Save and Edit */}
			{isEditable ? (
			<button
			className={`${styles.save_btn} ${styles.text_btn}`}
			onClick={handleSave}
			disabled={isLoading}
		>
			Save
		</button>
		) : (
		<button
			className={`${styles.edit_btn} ${styles.text_btn}`}
			onClick={handleEdit}
		>
			Edit
   </button>
			)}
			</div>
		)}
		</div>
	</>
	);
};

