import { useState } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import styles from "./styles.module.css";

const Signup = () => {
	const [step, setStep] = useState(1); // Track the current step
	const [data, setData] = useState({
		firstName: "",
		lastName: "",
		age: "",
		gender: "",
		nativeLanguage: "",
		educationLevel: "",
		literacyLevel: "",
		linguisticsTraining: "",
		simpleLanguageTraining: ""
	});
	const [error, setError] = useState("");
	const navigate = useNavigate();

	const handleChange = ({ currentTarget: input }) => {
		setData({ ...data, [input.name]: input.value });
	};

	const handleNext = () => {
		setStep(step + 1); // Move to the next step
	};

	const handleBack = () => {
		setStep(step - 1); // Move to the previous step
	};

	const handleSubmit = async (e) => {
		e.preventDefault();
		try {
			const url = "http://localhost:8080/api/users";
			const { data: res } = await axios.post(url, data);
			navigate("/login");
			console.log(res.message);
		} catch (error) {
			if (
				error.response &&
				error.response.status >= 400 &&
				error.response.status <= 500
			) {
				setError(error.response.data.message);
			}
		}
	};

	return (
		<div className={styles.main_container}>
			{/* Navbar */}
			<nav className={styles.navbar}>
				<h1>Text Simplification Study</h1>
			</nav>

			{/* Signup Form */}
			<div className={styles.signup_container}>
				<div className={styles.signup_form_container}>
					<div className={styles.left}>
						<h1>Welcome Back</h1>
						<Link to="/login">
							<button type="button" className={styles.white_btn}>
								Sign in
							</button>
						</Link>
					</div>
					<div className={styles.right}>
						<form className={styles.form_container} onSubmit={step === 3 ? handleSubmit : handleNext}>
							<h1>Create Account - Step {step}</h1>

							{step === 1 && (
								// Step 1 Fields
								<>
									<input
										type="text"
										placeholder="First Name"
										name="firstName"
										onChange={handleChange}
										value={data.firstName}
										required
										className={styles.input}
									/>
									<input
										type="text"
										placeholder="Last Name"
										name="lastName"
										onChange={handleChange}
										value={data.lastName}
										required
										className={styles.input}
									/>
									<input
										type="number"
										placeholder="Age"
										name="age"
										onChange={handleChange}
										value={data.age}
										required
										className={styles.input}
									/>
									<div className={styles.radio_group}>
										<label>What's your gender?</label>
										<div>
											<label>
												<input
													type="radio"
													name="gender"
													value="Female"
													onChange={handleChange}
													checked={data.gender === "Female"}
												/> Female
											</label>
											<label>
												<input
													type="radio"
													name="gender"
													value="Male"
													onChange={handleChange}
													checked={data.gender === "Male"}
												/> Male
											</label>
											<label>
												<input
													type="radio"
													name="gender"
													value="Non-binary"
													onChange={handleChange}
													checked={data.gender === "Non-binary"}
												/> Non-binary
											</label>
											<label>
												<input
													type="radio"
													name="gender"
													value="Self-describe"
													onChange={handleChange}
													checked={data.gender === "Self-describe"}
												/> Self-describe
											</label>
										</div>
									</div>
									<button type="button" onClick={handleNext} className={styles.nav_btn}>
										Next
									</button>
								</>
							)}

							{step === 2 && (
								// Step 2 Fields
								<>
									<label>Education Level</label>
									<select
										name="educationLevel"
										value={data.educationLevel}
										onChange={handleChange}
										className={styles.input}
										required
									>
										<option value="">Select Education Level</option>
										<option value="High School">High School</option>
										<option value="Bachelor's">Bachelor's</option>
										<option value="Master's">Master's</option>
										<option value="Doctorate">Doctorate</option>
									</select>

									<label>Native Language</label>
									<select
										name="nativeLanguage"
										value={data.nativeLanguage}
										onChange={handleChange}
										className={styles.input}
										required
									>
										<option value="">Select Native Language</option>
										<option value="English">English</option>
										<option value="Spanish">Spanish</option>
										<option value="French">French</option>
										{/* Add more options as needed */}
									</select>



									<label>Language Proficiency</label>
									<select
										name="literacyLevel"
										value={data.literacyLevel}
										onChange={handleChange}
										className={styles.input}
										required
									>
										<option value="">Select Literacy Level</option>
										<option value="Level 1" title="XYZ 1">Level 1 (Below Basic)</option>
										<option value="Level 2" title="XYZ 2">Level 2 (Basic)</option>
										<option value="Level 3" title="XYZ 3">Level 3 (Intermediate)</option>
										<option value="Level 4" title="XYZ 4">Level 4 (Proficient)</option>
										<option value="Level 5" title="XYZ 5">Level 5 (Advanced)</option>

										{/* <option value="Level 2">Level 2</option>
										<option value="Level 3">Level 3</option>
										<option value="Level 4">Level 4</option>
										<option value="Level 5">Level 5</option> */}
									</select>

									<div className={styles.button_container}>
										<button type="button" onClick={handleBack} className={styles.nav_btn}>
											Back
										</button>
										<button type="button" onClick={handleNext} className={styles.nav_btn}>
											Next
										</button>
									</div>
								</>
							)}

							{step === 3 && (
								// Step 3 Fields
								<>
									<div className={styles.radio_group}>
										<label>Training in Linguistics</label>
										<div>
											<label>
												<input
													type="radio"
													name="linguisticsTraining"
													value="Yes"
													onChange={handleChange}
													checked={data.linguisticsTraining === "Yes"}
												/> Yes
											</label>
											<label>
												<input
													type="radio"
													name="linguisticsTraining"
													value="No"
													onChange={handleChange}
													checked={data.linguisticsTraining === "No"}
												/> No
											</label>
										</div>
									</div>

									<div className={styles.radio_group}>
										<label>Training in Simple Language</label>
										<div>
											<label>
												<input
													type="radio"
													name="simpleLanguageTraining"
													value="Yes"
													onChange={handleChange}
													checked={data.simpleLanguageTraining === "Yes"}
												/> Yes
											</label>
											<label>
												<input
													type="radio"
													name="simpleLanguageTraining"
													value="No"
													onChange={handleChange}
													checked={data.simpleLanguageTraining === "No"}
												/> No
											</label>
										</div>
									</div>

									<div className={styles.button_container}>
										<button type="button" onClick={handleBack} className={styles.nav_btn}>
											Back
										</button>
										<button type="submit" className={styles.nav_btn}>
											Sign Up
										</button>
									</div>
								</>
							)}
						</form>
					</div>
				</div>
			</div>
		</div>
	);
};

export default Signup;



