import { useState } from "react";
import styles from "./styles.module.css";
import { useNavigate } from "react-router-dom";
import * as pdfjsLib from "pdfjs-dist/legacy/build/pdf";
import { jwtDecode } from "jwt-decode";


pdfjsLib.GlobalWorkerOptions.workerSrc = `${process.env.PUBLIC_URL}/pdf.worker.mjs`;

const Main = () => {
	const [inputText, setInputText] = useState("");
	const [outputText, setOutputText] = useState("");
	const [isLoading, setIsLoading] = useState(false);
	const [isSubmitted, setIsSubmitted] = useState(false);
	const [isEditable, setIsEditable] = useState(false); // For toggling edit mode
	const [uploadedFileName, setUploadedFileName] = useState(""); // State for file name
	const [isSaveButtonVisible, setIsSaveButtonVisible] = useState(true);
	const [showSurveyPrompt, setShowSurveyPrompt] = useState(false); // State for survey prompt


    const navigate = useNavigate();

	const handleLogout = () => {
	  localStorage.removeItem("token");
	  window.location.reload();
	};

	const handleSubmit = async () => {
	  if (!inputText.trim()) return;
	  setIsLoading(true);

	  try {
		const prompt = `Simplify the following text for better readability while preserving meaning:\n\n"${inputText}"`;

		const response = await fetch("http://localhost:5001/api/gpt4", {
		  method: "POST",
		  headers: { "Content-Type": "application/json" },
		  body: JSON.stringify({ prompt }),
		});

		const data = await response.json();
		const cleanedResponse =
		  data?.response?.replace(/^"|"$/g, "") || "No response received.";
		setOutputText(cleanedResponse);
		setIsSubmitted(true);
	  } catch (error) {
		console.error("Error fetching GPT-4 response:", error);
		setOutputText("An error occurred while fetching the response.");
	  }

	  setIsLoading(false);
	};

	const handleSave = async () => {
		setIsSaveButtonVisible(false);
		try {
		  const user = JSON.parse(localStorage.getItem("user")); // Retrieve user info
		  if (!user) {
			alert("User not logged in!");
			return;
		  }

		  const payload = {
			userId: user._id,
			email: user.email,
			inputText,
			...(isEditable ? { editedText: outputText } : { outputText, editableText: outputText }),
		  };

		  const endpoint = isEditable
			? "http://localhost:8080/api/simplifications/edit" // Endpoint for updating
			: "http://localhost:8080/api/simplifications"; // Endpoint for initial save

		  const method = isEditable ? "PUT" : "POST"; // Use PUT for updates, POST for saves

		  const response = await fetch(endpoint, {
			method,
			headers: { "Content-Type": "application/json" },
			body: JSON.stringify(payload),
		  });

		  if (response.ok) {
			const data = await response.json();
			alert(isEditable ? "Edited text saved successfully!" : "Simplification saved successfully!");

			if (!isSaveButtonVisible) {
				setShowSurveyPrompt(true); // Show the survey prompt
			  }

			if (isEditable) 
			 {setIsEditable(false); // Exit editing mode after saving edits
			  // disable saving button
			  setIsSaveButtonVisible(false); // Hide the save button
			   // Show the survey prompt
			   setShowSurveyPrompt(true); 
			 }
		  } else {
			const error = await response.json();
			alert(error.message || "Failed to save simplification.");
		  }
		} catch (error) {
		  console.error("Error saving simplification:", error);
		  alert("An error occurred. Please try again.");
		}

		setShowSurveyPrompt(true);
		// Page for survey (only save -- 2 boxes, else edit+save -- 3 boxes) -- fetch inputText, outputText and editedText. If outputText is same as editedText, then show only 2 text boxes, else 3 text boxes (all not editable) with 5 questions below.
		        // Navigate to survey page

		//navigate("/survey", { state: { email: user.email, inputText } });
	  };



	const handleEdit = () => {

	  handleSave(); // Save the current output text

	  setIsEditable(true); // Enable editing mode
	  setIsSaveButtonVisible(true);

	  // Add back button
	};

	const handleFileUpload = async (event) => {
	  const file = event.target.files[0];
	  if (!file) return;

	  try {
		setUploadedFileName(file.name); // Set the uploaded file name
		const fileReader = new FileReader();
		fileReader.onload = async function () {
		  const typedArray = new Uint8Array(this.result);

		  const pdf = await pdfjsLib.getDocument(typedArray).promise;
		  let text = "";

		  // Extract text from all pages
		  for (let i = 1; i <= pdf.numPages; i++) {
			const page = await pdf.getPage(i);
			const textContent = await page.getTextContent();
			text += textContent.items.map((item) => item.str).join(" ");
		  }

		  setInputText(text);
		};
		fileReader.readAsArrayBuffer(file);
	  } catch (error) {
		console.error("Error reading PDF:", error);
		alert("Failed to extract text from the PDF.");
	  }
	};

	return (
	  <>
		{/* Navbar */}
		<nav className={styles.navbar}>
		  <h1>Text Simplification Study</h1>
		  <button className={styles.white_btn} onClick={handleLogout}>
			Logout
		  </button>
		</nav>

		{/* Main Content */}
		<div className={styles.main_container}>
		  {!isSubmitted ? (
			<div className={styles.description}>
			  <p>
				This app is about simplifying text for improved readability and
				accessibility.
			  </p>
			  <p>
				Upload a document or paste your text to get a simplified version
				with just a click.
			  </p>

			  <div className={styles.form_container}>
				{/* Input Area */}
				<div className={styles.input_area}>
				  <div className={styles.text_container}>
					<label className={styles.label} htmlFor="inputText">
					  Input Text
					</label>
					<textarea
					  id="inputText"
					  className={styles.textarea}
					  placeholder="Original Text"
					  value={inputText}
					  onChange={(e) => setInputText(e.target.value)}
					></textarea>
				  </div>

				  {/* OR Divider */}
				  <div className={styles.or_divider}>OR</div>

				  {/* File Upload */}
				  <div className={styles.upload_area}>
					<label htmlFor="fileUpload" className={styles.upload_box}>
					  {uploadedFileName ? (
						<>
						  File uploaded: <strong>{uploadedFileName}</strong>
						</>
					  ) : (
						<>
						  Click to Upload a PDF
						  <br />
						  <span>...or drag and drop a file.</span>
						</>
					  )}
					</label>
					<input
					  type="file"
					  id="fileUpload"
					  accept="application/pdf"
					  onChange={handleFileUpload}
					  className={styles.hidden_input}
					/>
				  </div>
				</div>
			  </div>
			  {/* Submit Button */}
			  <button
				className={styles.submit_btn}
				onClick={handleSubmit}
				disabled={!inputText.trim() || isLoading}
			  >
				{isLoading ? "Processing..." : "Simplfiy Text"}
			  </button>
			</div>
		  ) : (
			<div className={styles.form_container}>
			  {/* Input and Output Boxes */}
			  <div className={styles.textareas_container}>
				{/* Input Text Box */}
				<div className={styles.text_container}>
				  <label className={styles.label} htmlFor="inputText">
					Input Text
				  </label>
				  <textarea
					id="inputText"
					className={`${styles.textarea} ${styles.side_by_side}`}
					value={inputText}
					onChange={(e) => setInputText(e.target.value)}
					readOnly
				  ></textarea>
				</div>

				{/* Output Text Box */}
				<div className={styles.text_container}>
				  <label className={styles.label} htmlFor="outputText">
					System-generated Text
				  </label>
				  <textarea
					id="outputText"
					className={`${styles.output_box} ${styles.side_by_side}`}
					value={outputText}
					onChange={(e) => setOutputText(e.target.value)}
					readOnly={!isEditable}
					placeholder="Output"
				  ></textarea>
				</div>
			  </div>


				{/* Save and Edit Buttons */}
				<div className={styles.button_container}>
				{/* <button
					className={`${styles.save_btn} ${styles.text_btn}`}
					onClick={handleSave}
				>
					Save
				</button> */}

				{isSaveButtonVisible && (
				<button
					className={`${styles.save_btn} ${styles.text_btn}`}
					onClick={handleSave}
					disabled={isLoading}
				>
					Save
				</button>
				)}

				{!isEditable && (
					<button
					className={`${styles.edit_btn} ${styles.text_btn}`}
					onClick={handleEdit}
					>
					Edit
					</button>
				)}
				</div>
      {/* Survey Prompt */}
	  {showSurveyPrompt && (
              <div className={styles.survey_prompt}>
                <p>
                  Please take the survey to help us improve.{" "}
                  <button
                    className={styles.link_btn}
                    onClick={() =>
                      navigate("/survey", {
                        state: { email: JSON.parse(localStorage.getItem("user")).email, inputText },
                      })
                    }
                  >
                    Take the Survey
                  </button>
                </p>
              </div>
            )}

			</div>
		  )}
		</div>
	  </>
	);
  };

export default Main;

