import React, { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import styles from "./styles.module.css";



const handleFormSubmit = (e) => {
  e.preventDefault();
  alert("Survey responses saved");
};

const SurveyPage = () => {
  const { state } = useLocation();
  //const { email, inputText } = state || {};
  const { email, inputText, outputText: initialOutputText, editHistory } = state || {};

  const [data, setData] = useState(null);
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/Login"); 
  };

  useEffect(() => {
    const fetchSimplification = async () => {
      try {
        const response = await fetch("http://localhost:8080/api/simplifications/fetch", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ email, inputText }),
        });

        const result = await response.json();

        if (response.ok) {
          setData(result.data);
        }
      } catch (error) {
        console.error("Error fetching simplification data:", error);
      }
    };

    if (email && inputText) {
      fetchSimplification();
    }
  }, [email, inputText]);


  if (!data)
    {return <p>Loading...</p>;}

  // const { inputText: input, outputText, editedText } = data;
  const { inputText: input, outputText, latestFinalText } = data;

  return (
    <>
		{/* Navbar */}
		<nav className={styles.navbar}>
		  <h1>Text Simplification Study</h1>
		  <button className={styles.white_btn} onClick={handleLogout}>
			Logout
		  </button>
		</nav>

    <div className={styles.main_container}>
    <div className={styles.description}>
			  <p>
        Kindly review the outputs below, and answer the questions below. Your feedback is valuable to us.    
        </p>
            <br></br>
    <div className={styles.form_container}>
     			  {/* Input and Output Boxes */}
     			  <div className={styles.textareas_container}>
              {/* Input Text Box */}
              <div className={styles.text_container}>
                <label className={styles.label} htmlFor="inputText">
                Input Text
                </label>

                <textarea id="inputText" className={`${styles.textarea} ${styles.side_by_side}`} value={input} readOnly placeholder="Input Text" />

              </div>

              {/* Output Text Box */}
              <div className={styles.text_container}>
                <label className={styles.label} htmlFor="outputText">
                System-generated Text
                </label>
                <textarea id="outputText" className={`${styles.output_box} ${styles.side_by_side}`} value={outputText} readOnly placeholder="Initial Generated Text" />

              </div>

              {/* {outputText !== editedText && (
                <div className={styles.text_container}>
                <label className={styles.label} htmlFor="outputText">
                Submitted Final Text
                </label>
                <textarea id="EditedText" className={`${styles.output_box} ${styles.side_by_side}`} value={editedText} readOnly placeholder="Final Edited Text" />

              </div>
              )} */}

      <div className={styles.text_container}>
      <label className={styles.label} htmlFor="outputText">Final Edited Text</label>
        {/* <textarea value={latestFinalText} readOnly /> */}
        <textarea id="EditedText" className={`${styles.output_box} ${styles.side_by_side}`} value={latestFinalText} readOnly/>
      </div>
    			  </div>

      <div>

			<h3 class="centered">Survey</h3>

{/* <form onSubmit={handleFormSubmit}> */}

<form class="survey-form" onSubmit={handleFormSubmit}>

  <div class="survey-question">
    <label>Is the output text clear and free of confusing language?</label>
    <div class="survey-options">
      <label><input type="radio" name="clarity" value="not-clear" /> Not clear at all</label>
      <label><input type="radio" name="clarity" value="somewhat-clear" /> Somewhat clear</label>
      <label><input type="radio" name="clarity" value="moderately-clear" /> Moderately clear</label>
      <label><input type="radio" name="clarity" value="mostly-clear" /> Mostly clear</label>
      <label><input type="radio" name="clarity" value="very-clear" /> Very clear</label>
    </div>
    {/* <div class="unsure-option">
      <label><input type="radio" name="clarity" value="unsure" /> Unsure</label>
    </div> */}
  </div>
<div>
  <br></br>
</div>
  <div class="survey-question">
    <label>Does the output text preserve the original meaning?</label>
    <div class="survey-options">
      <label><input type="radio" name="meaning" value="not-at-all" /> Not at all</label>
      <label><input type="radio" name="meaning" value="somewhat-preserves" /> Somewhat preserves</label>
      <label><input type="radio" name="meaning" value="moderately-preserves" /> Moderately preserves</label>
      <label><input type="radio" name="meaning" value="mostly-preserves" /> Mostly preserves</label>
      <label><input type="radio" name="meaning" value="completely-preserves" /> Completely preserves</label>
    </div>
    {/* <div class="unsure-option">
      <label><input type="radio" name="meaning" value="unsure" /> Unsure</label>
    </div> */}
  </div>
<div>
  <br></br>
</div>
  <div class="survey-question">
    <label>Does the output text meet your needs?</label>
    <div class="survey-options">
      <label><input type="radio" name="needs" value="not-at-all" /> Not at all</label>
      <label><input type="radio" name="needs" value="somewhat-meets" /> Somewhat meets</label>
      <label><input type="radio" name="needs" value="moderately-meets" /> Moderately meets</label>
      <label><input type="radio" name="needs" value="mostly-meets" /> Mostly meets</label>
      <label><input type="radio" name="needs" value="completely-meets" /> Completely meets</label>
    </div>
    {/* <div class="unsure-option">
      <label><input type="radio" name="needs" value="unsure" /> Unsure</label>
    </div> */}
  </div>
<div>
  <br></br>
</div>
  <div class="survey-question">
    <label>Are there grammatical errors in the output text?</label>
    <div class="survey-options">
      <label><input type="radio" name="grammar" value="many-errors" /> Many errors</label>
      <label><input type="radio" name="grammar" value="several-errors" /> Several errors</label>
      <label><input type="radio" name="grammar" value="some-errors" /> Some errors</label>
      <label><input type="radio" name="grammar" value="few-errors" /> Few errors</label>
      <label><input type="radio" name="grammar" value="no-errors" /> No errors</label>
    </div>
    {/* <div class="unsure-option">
      <label><input type="radio" name="grammar" value="unsure" /> Unsure</label>
    </div> */}
  </div>
<div>
  <br></br>
</div>
  <div class="survey-question">
    <label>Does the output text follow the IDD guidelines for sentence structure and organization?</label>
    <div class="survey-options">
    <label><input type="radio" name="guidelines" value="not-at-all" /> Not at all</label>
      <label><input type="radio" name="guidelines" value="somewhat-follows" /> Somewhat follows</label>
      <label><input type="radio" name="guidelines" value="moderately-follows" /> Moderately follows</label>
      <label><input type="radio" name="guidelines" value="mostly-follows" /> Mostly follows</label>
      <label><input type="radio" name="guidelines" value="completely-follows" /> Completely follows</label>
    </div>
    {/* <div class="unsure-option">
      <label><input type="radio" name="guidelines" value="unsure" /> Unsure</label>
    </div> */}
  </div>
<div>
  <br></br>
</div>
  <div class="survey-question">
    <label>Is the output text coherent in terms of sentence flow and organization?</label>
    <div class="survey-options">
    <label><input type="radio" name="coherent" value="not-at-all" /> Not at all</label>
      <label><input type="radio" name="coherent" value="somewhat-follows" /> Somewhat coherent</label>
      <label><input type="radio" name="coherent" value="moderately-follows" /> Moderately coherent</label>
      <label><input type="radio" name="coherent" value="mostly-follows" /> Mostly coherent</label>
      <label><input type="radio" name="coherent" value="completely-follows" /> Completely coherent</label>
    </div>
    {/* <div class="unsure-option">
      <label><input type="radio" name="grammar" value="unsure" /> Unsure</label>
    </div> */}
  </div>
<div>
  <br></br>
</div>
  <div class="survey-question">
    <label><strong>[OPTIONAL]</strong> How much effort was needed to edit the simplified text to make it acceptable?</label>
    <div class="survey-options">
      <label><input type="radio" name="editing_effort" value="many-errors" /> A lot of effort</label>
      <label><input type="radio" name="editing_effort" value="several-errors" /> Significant effort</label>
      <label><input type="radio" name="editing_effort" value="some-errors" /> Moderate effort</label>
      <label><input type="radio" name="editing_effort" value="few-errors" /> Minimal effort</label>
      <label><input type="radio" name="editing_effort" value="no-errors" /> No effort at all</label>
    </div>
    {/* <div class="unsure-option">
      <label><input type="radio" name="grammar" value="unsure" /> Unsure</label>
    </div> */}
  </div>

<div>
  <br></br>
</div>
  <div class="survey-question">
    <label>Do you have any additional comments about the output text? (Open-ended)</label>
    <textarea name="comments" rows="4" cols="50" placeholder="Enter your comments here..."></textarea>
  </div>

  <div>
    <button className={styles.submit_button} type="submit">Submit</button>
  </div>
</form>


      </div>
      </div>
    </div>
    </div>

    </>
  );
};

export default SurveyPage;
